package com.nivelle.core.javacore.lang;

import java.util.ArrayList;
import java.util.List;
import java.util.Locale;
import java.util.StringJoiner;

/**
 * String
 *
 * @author nivelle
 * @date 2019/06/16
 */
public class StringMock {


    /**
     * 1. String 代表的是一个字符数组,所有的java中的文字都是通过String类实现的。
     * 2. Strings are constant; their values cannot be changed after they are created. String buffers support mutable strings.
     * 3. Because String objects are immutable they can be shared
     *
     * @param args
     */
    public static void main(String[] args) {

        /**
         * String str = new String(“abc”) 这种方式，首先在编译类文件时，"abc"常量字符串将会放入到常量结构中，在类加载时，“abc"将会在常量池中创建；
         * 其次，在调用 new 时，JVM 命令将会调用 String 的构造函数，同时引用常量池中的"abc” 字符串，在堆内存中创建一个 String 对象；最后，str 将引用 String 对象。
         */
        String string1 = new String("nivelle");
        String string2 = "nivelle";
        System.out.print("常量池和对象比较:");
        System.out.println(string1 == string2);
        System.out.print("内容比较:");
        System.out.println(string1.equals(string2));
        System.out.print("强制比较池常量:");
        System.out.println(string1.intern() == string2);

        int lastIndexOf4 = string1.lastIndexOf(46);
        System.out.println("lastIndexOf4 is:" + lastIndexOf4);
        String a = new String("abc").intern();

        String b = new String("abc").intern();

        if (a == b) {
            /**
             * 1. 创建 a 变量时，调用 new Sting() 会在堆内存中创建一个 String 对象，String 对象中的 char 数组将会引用常量池中字符串。在调用 intern 方法之后，会去常量池中查找是否有等于该字符串对象的引用，有就返回引用。
             * 2. 创建 b 变量时，调用 new Sting() 会在堆内存中创建一个 String 对象，String 对象中的 char 数组将会引用常量池中字符串。在调用 intern 方法之后，会去常量池中查找是否有等于该字符串对象的引用，有就返回引用。
             *    而在堆内存中的两个对象，由于没有引用指向它，将会被垃圾回收。所以 a 和 b 引用的是同一个对象。
             */
            System.out.println("a==b is true");
        }

        /**
         * 字符串底层数据结构是字符串数组
         */
        char[] string3 = {'n', 'i', 'v', 'e', 'l', 'l', 'e'};
        System.out.println("string1对比string3:" + string1.equals(string3));
        System.out.println("string1对比string3:" + string1.equals(new String(string3)));

        /**
         * 由字符数组构造字符串
         * Allocates a new {@code String} that contains characters from a subarray of the character array argument.
         * offset:子字符串的初始位置
         * count:子字符串的长度
         */
        System.out.println("通过字符数组构造字符串:" + new String(string3, 1, string3.length - 1));
        /**
         * 通过字符ASCII码构造字符串
         */
        int[] postArray = {110, 105, 118, 101, 108, 108, 101};
        System.out.println("通过字符数组构造字符串:" + new String(postArray, 1, string3.length - 1));


        /**
         * 空参数构造函数相当于字符串数组=""赋值;
         */
        String string4 = new String();
        System.out.println("空构造函数：" + string4.equals(""));

        /**
         * hash码算法
         * 算法：hashCode = s[0]*31^(n-1) + s[1]*31^(n-2) + ... + s[n-1]， n 是字符串长度
         * 特例：The hash value of the empty string is zero
         */
        System.out.println("字符串string1的hash码:" + string1.hashCode());
        System.out.println("字符串string2的hash码:" + string2.hashCode());
        System.out.println("空字符串string4的hash码等于0:" + string4.hashCode());

        /**
         * 字符转字节编码数组
         */
        byte[] bytes = string1.getBytes();
        for (int i = 0; i < bytes.length; i++) {
            System.out.print("字符串ASCII码:" + bytes[i] + ";");
        }
        System.out.println();
        /**
         * 获取指定位置的字符
         */
        System.out.println("指定位置的字符:" + string1.charAt(1));
        /**
         * 返回指定位置的字节编码
         */
        System.out.println("指定位置的字符:" + string1.codePointAt(1));
        /**
         * 忽略大小写等于比较，实现上：默认都是转化为大写字母比较**
         */
        System.out.println("字符串忽略大小写相等判断:" + string1.equalsIgnoreCase(string2));

        /**
         * The comparison is based on the Unicode value of each character in the strings
         * 字符串比较默认基于 字符集
         */
        String compare1 = "A";
        String compare2 = "C";
        System.out.println("字符串字典比较:" + compare1.compareTo(compare2));

        /**
         * 大小写不敏感比较，分别转为大写字母和小写字母分别比较一次
         */
        String compare3 = "100";
        String compare4 = "97";
        System.out.println("字符串忽略大小写大小比较:" + compare3.compareToIgnoreCase(compare4));

        /**
         * 切割字符串
         * 底层利用了: public String(char value[], int offset, int count) 构造函数
         */
        System.out.println("切割字符串:" + string2.substring(0, 1));


        /**
         * 字符串拼接,需要经过两次字符串复制:
         * 1. char buf[] = Arrays.copyOf(value, len + otherLen); =>buf[] 字符数组包括 string2字符串+加上新字符串的长度
         * 2. str.getChars(buf, len);=> "love jessy!".getChars(buf,len);
         * 3. System.arraycopy(value, 0, dst, dstBegin, value.length); =>love jessy! 这个字符串调用复制函数
         */
        System.out.println("拼接字符串:" + string2.concat(" love jessy!"));

        /**
         * 字符串替换
         */
        System.out.println("字符串替换:" + string2.replace("l", "j"));

        /**
         * 字符串匹配
         */
        System.out.println("字符串匹配:" + string2.matches("nivelle"));

        /**
         * 字符串分割成数组
         */
        String string5 = "n,i,v,e,l,l,e";
        for (int i = 0; i < string5.split(",").length; i++) {
            System.out.print("字符分割:" + string5.split(",")[i] + ";");
        }
        System.out.println();

        /**
         * 集合转指定字符拼接的字符串
         */
        List list = new ArrayList();
        list.add("n");
        list.add("i");
        list.add("v");
        list.add("e");
        list.add("l");
        list.add("l");
        list.add("e");
        String joinResult = String.join(",", list);
        System.out.println("集合元素通过指定符号拼接:" + joinResult);

        StringJoiner joiner = new StringJoiner(";");
        for (Object cs : list) {
            joiner.add((CharSequence) cs);
        }
        System.out.println("集合元素通过指定符号拼接原理:" + joiner.toString());

        /**
         * 字符串字母转换:大写->小写
         */
        System.out.println("字符串转小写:" + "NIVELLE".toLowerCase(Locale.US));
        /**
         * 字符串字母转换:小写->大写
         */
        System.out.println("字符串转大写:" + "nivellE".toUpperCase(Locale.US));

        /**
         * 去掉字符串首尾中的空格
         */
        System.out.println("去掉字符串首尾中的空格:" + " nive  ll e ".trim());

        /**
         * 底层原理:通过复制当前字符窜的字符串数组实现：
         * System.arraycopy(value, 0, result, 0, value.length);
         */
        for (int i = 0; i < "nivelle".length(); i++) {
            System.out.println("转为字符串数组:" + ("nivelle".toCharArray()[i]));
        }

        System.out.println("格式化字符串:" + String.format("nivelle%s", "e"));

        String str = new String("aaa");
        System.out.println(str);

        referenceStr(str);
        test01();
        test02();
        internTest();
        stringInit();

    }

    public static void changeStr(String str) {
        String s = str;
        str += "welcome";
        System.out.println(s);
        System.out.println(str);
    }


    public static void referenceStr(String str) {
        String strCopy = str;
        strCopy.toUpperCase();
        System.out.println(str);
    }

    private static void test01() {
        String s1 = "nivelle";
        String s2 = "nivelle";
        String s3 = "nive" + "lle";
        System.out.println("s1==s2:" + (s1 == s2));
        System.out.println("s1==s3:" + (s1 == s3));
    }

    private static void test02() {
        String s0 = "nivelle";
        String s1 = new String("nivelle");
        String s2 = "nive" + new String("lle");

        String s4 = "lle";
        String s3 = "nive";


        System.out.println("s0==s1:" + (s0 == s1));
        System.out.println("s0==s2:" + (s0 == s2));
        System.out.println("s1==s2:" + (s1 == s2));
        System.out.println("s0==s3:" + (s0 == s3));
        System.out.println("s1==s3:" + (s1 == s3));
        System.out.println("s2==s3:" + (s2 == s3));
        /**
         * java字符串String的最大长度，要分两个阶段，编译阶段及运行时阶段
         *
         * 编译阶段：
         *
         * 在我们使用字符串字面量直接定义String的时候，会把字符串在常量池中存储一份。常量池中的每一项常量都是一个表，都有自己对应的类型。
         * String类型，有一张固定长度的CONSTANT_String_info表用来存储文字字符串值，注意：该表只存储文字字符串值，不存储符号引用。
         *
         * JVM的常量池最多可放65535个项。第0项不用。最后一项最多只能是65534(下标值)。而每一项中，若是放一个UTF-8的常量串，其长度最长是:65535个字节(不是字符)。
         *
         *
         *
         * 运行时阶段：
         *
         * String内部是以char数组的形式存储，数组的长度是int类型，那么String允许的最大长度就是Integer.MAX_VALUE了,2147483647；又由于java中的字符是以16位存储的，因此大概需要4GB的内存才能存储最大长度的字符串。
         */
        Integer maxLength = maxStringLenth();
        System.err.println("java运行时字符串最大长度:" + maxLength);
        String maxString = "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" +
                "\n" +
                "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa";

        System.err.println("java编译期字符串最大长度:" + maxString.length());


    }

    private static void internTest() {
        String str1 = "abc";
        String str2 = new String("abc");

        String str3 = str2.intern();

        System.out.println("str1==str2:" + (str1 == str2));
        System.out.println("str1==str3:" + (str1 == str3));
    }

    private static void stringInit() {
        String s1 = "abc";
        String s3 = new String("abc");
        //创建了两个对象，一个存放在字符串池中，一个存在于堆中；
        //还有一个对象引用s3存放在栈中
        //字符串池中已经存在“abc”对象，所以只在堆中创建了一个对象
        String s4 = new String("abc");
        System.out.println("字符串比较");

        //false   s3和s4栈区的地址不同，指向堆区的不同地址；
        System.out.println("s3 == s4 : " + (s3 == s4));

        //true  s3和s4的值相同
        System.out.println("s3.equals(s4) : " + (s3.equals(s4)));

        //false 存放的地区都不同，一个方法区，一个堆区
        System.out.println("s1 == s3 : " + (s1 == s3));

        //true  值相同
        System.out.println("s1.equals(s3) : " + (s1.equals(s3)));
    }

    public static Integer maxStringLenth() {
        //
        System.err.println("字符串运行时限制于字符串数组最大元素数量， 2<sup>31</sup>-1:"+Integer.MAX_VALUE);
        return Integer.MAX_VALUE;
    }

    /**
     * 即使使用 + 号作为字符串的拼接，也一样可以被编译器优化成 StringBuilder 的方式。
     *
     * 在字符串常量中，默认会将对象放入常量池；在字符串变量中，对象是会创建在堆内存中，同时也会在常量池中创建一个字符串对象，String 对象中的 char 数组将会引用常量池中的 char 数组，并返回堆内存对象引用。
     */
}
